const categories = [
  { id: "all", label: "All" },
  { id: "backend", label: "Backend", techs: ["C#", ".NET", "Python", "JavaScript", "TypeScript"] },
  { id: "frontend", label: "Frontend", techs: ["React", "Angular", "Vue.js", "Next.js", "TypeScript"] },
  { id: "cloud", label: "Cloud", techs: ["AWS", "Azure", "GCP", "Docker", "Kubernetes", "Terraform"] },
  { id: "devops", label: "DevOps", techs: ["Docker", "Kubernetes", "Terraform", "Jenkins", "Git", "GitHub"] },
  { id: "database", label: "Database", techs: ["PostgreSQL", "MongoDB", "Redis"] },
  { id: "aiml", label: "AI/ML", techs: ["TensorFlow", "PyTorch", "OpenAI", "Hugging Face", "LangChain", "Scikit", "Python"] },
  { id: "observability", label: "Monitoring", techs: ["Grafana", "Prometheus", "Datadog"] },
];

const TechIcon = ({ name, isActive }: { name: string; isActive: boolean }) => {
  const [isHovered, setIsHovered] = useState(false);
  const tech = TechLogos[name];

  if (!tech) return null;

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: isActive ? 1 : 0.3, scale: isActive ? 1 : 0.85 }}
      transition={{ duration: 0.3 }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      className="relative flex-shrink-0 cursor-pointer"
    >
      <motion.div
        whileHover={{ scale: 1.25, y: -10 }}
        transition={{ type: "spring", stiffness: 400, damping: 15 }}
        className="w-12 h-12 md:w-14 md:h-14 rounded-xl flex items-center justify-center border border-border/20 backdrop-blur-sm transition-all duration-300"
        style={{
          background: isHovered 
            ? `linear-gradient(135deg, ${tech.color}20, ${tech.color}40)` 
            : `linear-gradient(135deg, ${tech.color}08, ${tech.color}15)`,
          boxShadow: isHovered ? `0 15px 40px ${tech.color}40` : "none",
        }}
      >
        <svg viewBox="0 0 24 24" className="w-6 h-6 md:w-7 md:h-7" fill={tech.color}>
          <path d={tech.path} />
        </svg>
      </motion.div>

      {/* Tooltip */}
      <AnimatePresence>
        {isHovered && (
          <motion.div
            initial={{ opacity: 0, y: 8, scale: 0.9 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 8, scale: 0.9 }}
            transition={{ duration: 0.15 }}
            className="absolute -bottom-10 left-1/2 -translate-x-1/2 px-3 py-1 rounded-lg text-[11px] font-bold whitespace-nowrap z-50 shadow-2xl"
            style={{
              background: tech.color,
              color: ["#F7DF1E", "#FFD21E", "#61DAFB", "#06B6D4", "#FF9900", "#4FC08D"].includes(tech.color) ? "#000" : "#fff",
            }}
          >
            {name}
            <div
              className="absolute -top-1 left-1/2 -translate-x-1/2 w-2 h-2 rotate-45"
              style={{ background: tech.color }}
            />
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

const InfiniteScrollRow = ({
  items,
  direction = "left",
  speed = 35,
  activeCategory,
}: {
  items: string[];
  direction?: "left" | "right";
  speed?: number;
  activeCategory: string;
}) => {
  const [isPaused, setIsPaused] = useState(false);
  const duplicatedItems = [...items, ...items, ...items];

  return (
    <div
      className="relative overflow-hidden py-3"
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      <div className="absolute left-0 top-0 bottom-0 w-32 bg-gradient-to-r from-muted/30 via-muted/20 to-transparent z-10 pointer-events-none" />
      <div className="absolute right-0 top-0 bottom-0 w-32 bg-gradient-to-l from-muted/30 via-muted/20 to-transparent z-10 pointer-events-none" />

      <motion.div
        className="flex gap-4"
        animate={{
          x: direction === "left" ? ["0%", "-33.33%"] : ["-33.33%", "0%"],
        }}
        transition={{
          x: {
            duration: speed,
            repeat: Infinity,
            ease: "linear",
          },
        }}
        style={{ animationPlayState: isPaused ? "paused" : "running" }}
      >
        {duplicatedItems.map((tech, index) => {
          const category = categories.find(c => c.id === activeCategory);
          const isActive = activeCategory === "all" || (category?.techs?.includes(tech) ?? false);
          return <TechIcon key={`${tech}-${index}`} name={tech} isActive={isActive} />;
        })}
      </motion.div>
    </div>
  );
};

export const TechStackShowcase = () => {
  const [activeCategory, setActiveCategory] = useState("all");
  const allTechs = Object.keys(TechLogos);
  const row1 = allTechs.slice(0, Math.ceil(allTechs.length / 2));
  const row2 = allTechs.slice(Math.ceil(allTechs.length / 2));

  return (
    <section className="py-12 md:py-16 bg-muted/30 overflow-hidden">
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          className="text-center mb-6"
        >
          <h2 className="text-2xl md:text-3xl lg:text-4xl font-black mb-2 tracking-tight">
            Technical <span className="gradient-text">Expertise</span>
          </h2>
          <p className="text-muted-foreground text-sm max-w-lg mx-auto mb-6">
            Core technologies I work with daily
          </p>

          {/* Category Filter */}
          <div className="flex flex-wrap justify-center gap-2">
            {categories.map((cat) => (
              <motion.button
                key={cat.id}
                onClick={() => setActiveCategory(cat.id)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className={`px-3 py-1.5 rounded-full text-xs font-semibold transition-all duration-300 ${
                  activeCategory === cat.id
                    ? "bg-primary text-primary-foreground shadow-lg shadow-primary/25"
                    : "bg-background/50 text-muted-foreground hover:bg-background hover:text-foreground border border-border/50"
                }`}
              >
                {cat.label}
              </motion.button>
            ))}
          </div>
        </motion.div>

        {/* Two scrolling rows */}
        <div className="space-y-1">
          <InfiniteScrollRow items={row1} direction="left" speed={40} activeCategory={activeCategory} />
          <InfiniteScrollRow items={row2} direction="right" speed={45} activeCategory={activeCategory} />
        </div>
      </div>
    </section>
  );
};
